mapper.select:
SELECT *
FROM tbl_user
WHERE
user_id = #{id}
AND user_pw = #{pw}

mapper.update:
UPDATE tbl_user
user_phone = #{userPhone}, user_id = #{userId}, user_pw = #{userPw}, user_name = #{userName},
WHERE user_no = #{userNo}

UPDATE tbl_user
SET user_pw = #{userPw}
WHERE user_no = #{userNo}

mapper.select:
